import React from 'react';
import Button from '../../src/index';
//import a11y from 'react-a11y';
import ReactDOM from 'react-dom';
// a11y(React, {ReactDOM: ReactDOM});

const hoverStyles = {
  hoverButtonStyles: {
    backgroundColor: '#fbfbfb',
    border: '1px solid #efefef'
  },

  hoverLabelStyles: {
    color: '#4ab7e2'
  },

  hoverSvgStyles: {
    fill: '#4ab7e2'
  }
};


const muteStyles = {
  buttonStyles : {
    backgroundColor: 'yellow'
  },
  labelStyles : {
    backgroundColor: 'pink',
    color: 'light blue'
  },
  svgStyles : {
    color: 'light green'
  }
};


//console.log('Button', Button)
describe('button ', function() {
  it('basic button ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}}/> );
  });
  it('basic button with default position and label ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"}/> );
  });
  it('basic button with default position, label and svg ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} svgType={'AddIconSVG'}/> );
  });
  it('basic button with bottom position, label and svg ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} svgPos="bottom" svgType={'AddIconSVG'}/> );
  });
  it('basic button with top position, label and svg ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} svgPos="top" svgType={'AddIconSVG'}/> );
  });
  it('basic button with right position, label and svg ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} svgPos="right" svgType={'AddIconSVG'}/> );
  });
  it('basic button that is disabled', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} isDisabled={true} svgPos="right" svgType={'AddIconSVG'}/> );
  });
  it('basic button with hover in compStyle ', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} svgPos="left" svgType={'AddIconSVG'} compStyle={hoverStyles}/> );
  });
  it('prioritizes componentStyle', () => {
    this.component(<Button cbClick={()=>{console.log('click')}} buttonLabel={"The Label"} svgPos="left" svgType={'AddIconSVG'} compStyle={muteStyles} /> );
  });
});
