import React from "react";
//import your component here
import Input from "../../src/index.js";


const enter = function(input){console.log(input);  console.log(input.length);};

const error = "This is a Test error. you have an error in your input field";

describe("Input Component", function() {


  before(() => {
    this.component(
        <Input cbOnEnter={enter} inputType={"text"} placeholder="placeholder"/>
    );
  });
  it("Reset", () => {
    this.component(
        <Input cbOnEnter={enter}  inputType={"text"} />
      );
  });
  it("onChange", () => {
    const myChange = (d) =>{
      console.log('input changed: ', d);
      this.component(
          <Input cbOnEnter={enter} value={d} inputType={"text"} cbOnChange={myChange} />);
    }

    this.component(
      <div style={{height: '200px', width: '200px', border: '3px solid black'}}>
        <Input cbOnEnter={enter}  inputType={"text"} cbOnChange={myChange} />
      </div>);
  });
  it("email input", () => {
    this.props({ inputType: 'email'});
  });
  it("text input", () => {
    this.props({ inputType: 'text'});
  });
  it("password input", () => {
    this.props({ inputType: 'password'});
  });
  it("custom input", () => {
    this.props({ inputType: 'text', svgOverride: 'SearchIconSVG'});
  });
  it("custom input with bad svgOverride name", () => {
    this.props({ inputType: 'text', svgOverride: 'whateverIconSVG'});
  });
  it("no custom input", () => {
    this.props({ inputType: 'text', svgOverride: undefined});
  });
  it("has Error", () => {
    this.props({ hasError: true, errorText: "This is a Test error. you have an error in your input field"});
  });
  it("has Error but no msg", () => {
    this.props({ hasError: true, errorText: undefined});
  });
  it("does not have Error", () => {
    this.props({ hasError: false, errorText: undefined});
  });
  it("placeholder", () => {
    this.props({ placeholder: "placeholder"});
  });
  it("invokes cbOnEnter", () => {
    this.props({ cbOnEnter: (input)=>{ console.log('cbOnEnter firing from test.', input); }});
  });

  it("has svg", () => {
    this.props({ hasSVG: true});
  });
  it("no svg", () => {
    this.props({ hasSVG: false});
  });
  it("disabled", () => {
    this.props({ isDisabled: true});
  });
  it("SvgArea without cbIconClick", () => {
    this.component(
      <Input cbOnEnter={enter} inputType={"text"} />
    );
  });
  it("SvgArea with cbIconClick", () => {
    this.component(
      <Input cbOnEnter={enter} cbIconClick={()=>{console.log('cbIconClick is clicked!');}} inputType={"email"} />
    );
  });
});
