// s2s-svg-container/__tests__/index-js

//'use strict';
import React from 'react';
import { shallow, mount, render } from 'enzyme';
import sinon from 'sinon';

jest.unmock('../src/index.jsx');
jest.unmock('../node_modules/s2s-base-class');
jest.unmock('../node_modules/sinon');
// jest.unmock('../node_modules/immutable');
// jest.unmock('../node_modules/sinon');
import ReactDOM from 'react-dom';
import {
  ContainerIconSVG,
  ContainerFullLogoSVG,
  ContainerMarkLogoSVG
} from '../src/index.jsx';

//These tests will simply make sure that each container will contain an SVG
describe('s2s-svg-toggle tests', ()=>{

  it('renders ContainerIconSVG', () => {
    const wrapper = mount(<div><ContainerIconSVG title='test' description='this is for testing' /></div>);
    expect(wrapper.find('svg').length).toEqual(1);
  });

  it('renders ContainerFullLogoSVG', () => {
    const wrapper = mount(<div><ContainerFullLogoSVG title='test' description='this is for testing' /></div>);
    expect(wrapper.find('svg').length).toEqual(1);
  });

  it('renders ContainerMarkLogoSVG', () => {
    const wrapper = mount(<div><ContainerMarkLogoSVG title='test' description='this is for testing' /></div>);
    expect(wrapper.find('svg').length).toEqual(1);
  });

});
