//'use strict';
import React from 'react';
import { shallow, mount, render } from 'enzyme';
import sinon from 'sinon';
jest.unmock('../src/index.jsx');
// jest.unmock('../node_modules/sinon');

import {default as Theme, ApplyTheme} from '../src/index.jsx';

describe('Theme Component', ()=>{

 it('wrapper applies theme', () => {
   var MyComp = ApplyTheme(class extends React.Component {
     render(){
       return (<p>Hello world!</p>);
     }
   });
   const wrapper = mount(<Theme appliedStyle={{color: 'purple'}} >
                             <MyComp />
                         </Theme>);
   expect(wrapper.node.props.appliedStyle.color).toEqual('purple');
 });

 it('renders the correct information when withRef is set to true', () => {
   var MyComp = ApplyTheme(class extends React.Component {
     render(){
       return (<p>Hello world!</p>);
     }
   }, {withRef: true} );
   const wrapper = mount(<Theme appliedStyle={{color: 'purple'}} >
                            <MyComp />
                          </Theme>);
   //console.log('<><><><><>><> refs are: ', wrapper.find('ThemeFromContext').node.getWrappedInstance());
   expect(wrapper.find('ThemeFromContext').node.getWrappedInstance());
 });

});

